// Placeholder for Day.js library
// In production, download from: https://cdn.jsdelivr.net/npm/dayjs@1/dayjs.min.js
// Simple implementation for basic usage
const dayjs = (date) => {
    const d = date ? new Date(date) : new Date();
    return {
        format: (formatStr) => {
            const day = String(d.getDate()).padStart(2, '0');
            const month = String(d.getMonth() + 1).padStart(2, '0');
            const year = d.getFullYear();
            const monthNames = ['January', 'February', 'March', 'April', 'May', 'June', 
                              'July', 'August', 'September', 'October', 'November', 'December'];
            const monthName = monthNames[d.getMonth()];
            
            return formatStr
                .replace('YYYY', year)
                .replace('MM', month)
                .replace('DD', day)
                .replace('MMMM', monthName);
        },
        startOf: (unit) => {
            const newDate = new Date(d);
            if (unit === 'month') {
                newDate.setDate(1);
                newDate.setHours(0, 0, 0, 0);
            }
            return dayjs(newDate);
        },
        endOf: (unit) => {
            const newDate = new Date(d);
            if (unit === 'month') {
                newDate.setMonth(newDate.getMonth() + 1);
                newDate.setDate(0);
                newDate.setHours(23, 59, 59, 999);
            }
            return dayjs(newDate);
        },
        date: (day) => {
            const newDate = new Date(d);
            newDate.setDate(day);
            return dayjs(newDate);
        },
        subtract: (amount, unit) => {
            const newDate = new Date(d);
            if (unit === 'month') {
                newDate.setMonth(newDate.getMonth() - amount);
            }
            return dayjs(newDate);
        },
        add: (amount, unit) => {
            const newDate = new Date(d);
            if (unit === 'month') {
                newDate.setMonth(newDate.getMonth() + amount);
            }
            return dayjs(newDate);
        },
        isSame: (other, unit) => {
            const otherDate = typeof other === 'function' ? other() : new Date(other);
            if (unit === 'day') {
                return d.toDateString() === otherDate.toDateString();
            }
            return d.getTime() === otherDate.getTime();
        },
    };
};

dayjs.extend = () => dayjs;
// Экспортируем в window для использования в обычных скриптах и модулях
if (typeof window !== 'undefined') {
    window.dayjs = dayjs;
}
// Экспортируем для CommonJS модулей
if (typeof module !== 'undefined' && module.exports) {
    module.exports = dayjs;
}
